  function App() {
const [theme,settheme]=useState();
const toggletheme = ()=>{
  settheme(!theme)
}
const themeclass= theme? "light":"dark";

  return (
    <div className= {`App  ${themeclass}`}> 
       <button onClick={toggletheme}>Toggle Theme</button>
          <section className='main' >
      <div className='container'>
      <Header  />

        <Todolist/>
        <Footer/>  
      </div>
      </section>
    </div>

  );
};  

export default App;
